tables.table_schema:
CREATE TABLE species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL
);

CREATE TABLE genetic_data (
  genetic_id INT PRIMARY KEY,
  individual_id INT REFERENCES individuals(individual_id),
  genetic_sequence TEXT,
  mutation_rate FLOAT
);

CREATE TABLE population_data (
    sample_id INT PRIMARY KEY,
    population_name VARCHAR(255),
    location VARCHAR(255),
    genetic_variation FLOAT
);

CREATE TABLE migration_patterns (
    migration_id INT PRIMARY KEY,
    source_population VARCHAR(255),
    destination_population VARCHAR(255),
    migration_rate FLOAT
);

CREATE TABLE gene_markers (
    marker_id INT PRIMARY KEY,
    marker_name VARCHAR(255),
    chromosome_number INT,
    genetic_distance FLOAT
);

CREATE TABLE genetic_samples (
    sample_id INT PRIMARY KEY,
    marker_id INT,
    genotype VARCHAR(255),
   FOREIGN KEY (marker_id) REFERENCES gene_markers(marker_id)
);

CREATE TABLE population_relationship (
    relationship_id INT PRIMARY KEY,
    population1_name VARCHAR(255),
    population2_name VARCHAR(255),
    genetic_distance FLOAT
);
