tables.table_schema:
CREATE TABLE GeneExpressionData (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(255) NOT NULL,
  expression_level FLOAT
);

CREATE TABLE GeneExpressionData (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    expression_level FLOAT
);

CREATE TABLE ComparativeGenomicAnalysis (
    analysis_id INT PRIMARY KEY,
    gene_id INT,
    species_name VARCHAR(255),
    analysis_results TEXT
);

CREATE TABLE species_data (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL,
  common_name VARCHAR(50),
  description TEXT
);

CREATE TABLE GeneExpressions (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(50) NOT NULL,
    expression_level FLOAT NOT NULL
);

CREATE TABLE Species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL
);

CREATE TABLE Genomes (
    genome_id INT PRIMARY KEY,
    species_id INT NOT NULL,
    genome_size BIGINT,
    FOREIGN KEY (species_id) REFERENCES Species(species_id)
);

CREATE TABLE Experiments (
    experiment_id INT PRIMARY KEY,
    experiment_name VARCHAR(50) NOT NULL,
    species_id INT NOT NULL,
    FOREIGN KEY (species_id) REFERENCES Species(species_id)
);
