tables.table_schema:
CREATE TABLE ProteinData (
    id SERIAL PRIMARY KEY,
    protein_name VARCHAR(255) NOT NULL,
    sequence TEXT NOT NULL,
    molecular_weight FLOAT NOT NULL,
    mass_spec_data JSONB
);

CREATE TABLE QuantificationData (
    id SERIAL PRIMARY KEY,
    protein_id INTEGER REFERENCES ProteinData(id),
    experiment_date DATE NOT NULL,
    intensity FLOAT NOT NULL
);

CREATE TABLE proteins (
    id INT PRIMARY KEY,
    protein_name VARCHAR(255),
    molecular_weight DECIMAL,
    sequence TEXT
);

CREATE TABLE spectra (
    id INT PRIMARY KEY,
    spectrum_name VARCHAR(255),
    retention_time DECIMAL,
    intensity FLOAT
);

CREATE TABLE peptide_hits (
    id INT PRIMARY KEY,
    peptide_sequence TEXT,
    mass_error DECIMAL,
    confidence_level FLOAT
);

CREATE TABLE experimental_conditions (
    id INT PRIMARY KEY,
    experiment_name VARCHAR(255),
    temperature DECIMAL,
    ph DECIMAL
);
