);

CREATE TABLE experimental_conditions (
  condition_id INT PRIMARY KEY,
  condition_name VARCHAR(50) NOT NULL,
  treatment VARCHAR(100) NOT NULL
);

CREATE TABLE experimental_conditions (
    condition_id INT PRIMARY KEY,
    treatment_name VARCHAR(255),
    treatment_duration INT,
    treatment_description TEXT
);

CREATE TABLE experimental_conditions (
  condition_id INT PRIMARY KEY,
  condition_name VARCHAR(255),
  treatment_id INT,
  treatment_name VARCHAR(255)
);

CREATE TABLE protein_expression (
  protein_id INT PRIMARY KEY,
  protein_name VARCHAR(255),
  expression_level FLOAT,
  condition_id INT,
  FOREIGN KEY (condition_id) REFERENCES experimental_conditions(condition_id)
);

CREATE TABLE treatment_details (
  treatment_id INT PRIMARY KEY,
  treatment_name VARCHAR(255),
  treatment_description TEXT
);

CREATE TABLE experiment_results (
  result_id INT PRIMARY KEY,
  protein_id INT,
  condition_id INT,
  result_details TEXT,
  FOREIGN KEY (protein_id) REFERENCES protein_expression(protein_id),
  FOREIGN KEY (condition_id) REFERENCES experimental_conditions(condition_id)
);

CREATE TABLE experimental_conditions (
    condition_id INT PRIMARY KEY,
    treatment_name VARCHAR(50),
    treatment_duration INT,
    temperature FLOAT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    email VARCHAR(50)
);
