tables.table_schema:
CREATE TABLE proteomics_data_source (
  source_id UUID PRIMARY KEY,
  source_name VARCHAR(100) NOT NULL,
  source_description TEXT,
  created_date TIMESTAMP
);

CREATE TABLE Proteins (
	id INT PRIMARY KEY,
	protein_name VARCHAR(255),
	molecular_weight DECIMAL,
	sequence TEXT
);

CREATE TABLE Peptides (
	id INT PRIMARY KEY,
	peptide_sequence TEXT,
	protein_id INT,
	FOREIGN KEY (protein_id) REFERENCES Proteins(id)
);

CREATE TABLE Spectra (
	id INT PRIMARY KEY,
	spectrum_data TEXT,
	peptide_id INT,
	FOREIGN KEY (peptide_id) REFERENCES Peptides(id)
);

CREATE TABLE Experiments (
	id INT PRIMARY KEY,
	experiment_name VARCHAR(255),
	description TEXT,
	date_performed DATE
);
