tables.table_schema:
CREATE TABLE dna_sequence_alignments (
    id INT PRIMARY KEY,
    sequence_data VARCHAR(255),
    length INT,
    alignment_score FLOAT
);

CREATE TABLE dna_sequence_alignments (
    alignment_id UUID PRIMARY KEY,
    sequence_name VARCHAR(255),
    sequence_data TEXT
);

CREATE TABLE evolutionary_studies (
    study_id UUID PRIMARY KEY,
    study_name VARCHAR(255),
    researcher_name VARCHAR(255),
    analysis_results JSON
);

CREATE TABLE DNA_Sequences (
    sequence_id INT PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE Alignments (
    alignment_id INT PRIMARY KEY,
    sequence_id INT,
    alignment TEXT,
    FOREIGN KEY (sequence_id) REFERENCES DNA_Sequences(sequence_id)
);

CREATE TABLE alignment_sequences (
    alignment_id INT,
    sequence_id INT,
    sequence TEXT,
    score FLOAT
);

CREATE TABLE alignment_metadata (
    alignment_id INT,
    alignment_name VARCHAR(255),
    species_name VARCHAR(255),
    alignment_date DATE
);

CREATE TABLE sequence_annotations (
    annotation_id INT,
    sequence_id INT,
    annotation_text TEXT,
    annotation_date DATE
);

CREATE TABLE alignment_results (
    result_id INT,
    alignment_id INT,
    result_text TEXT,
    result_date DATE
);

CREATE TABLE alignment_sequences (
    alignment_id INT PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE alignment_scores (
    score_id INT PRIMARY KEY,
    alignment_id INT,
    score FLOAT
);

CREATE TABLE alignment_metadata (
    metadata_id INT PRIMARY KEY,
    alignment_id INT,
    date_created DATE,
    researcher_name TEXT
);

CREATE TABLE alignment_positions (
    position_id INT PRIMARY KEY,
    alignment_id INT,
    position_index INT,
    base_pair CHAR
);

CREATE TABLE alignment_results (
    result_id INT PRIMARY KEY,
    alignment_id INT,
    result_text TEXT
);
