tables.table_schema:
CREATE TABLE variants (
    variant_id UUID PRIMARY KEY,
    chrom VARCHAR(10),
    position INT,
    ref VARCHAR(50),
    alt VARCHAR(50)
);

CREATE TABLE genotypes (
    genotype_id UUID PRIMARY KEY,
    variant_id UUID REFERENCES variants(variant_id),
    sample_id UUID,
    genotype JSONB
);

CREATE TABLE genomics_data (
    sample_id INT PRIMARY KEY,
    chromosome INT,
    position INT,
    genotype VARCHAR(2)
);

CREATE TABLE association_results (
    analysis_id INT PRIMARY KEY,
    p_value FLOAT,
    effect_size FLOAT,
    significant BOOLEAN
);

CREATE TABLE clinical_data (
    sample_id INT PRIMARY KEY,
    age INT,
    sex VARCHAR(10),
    condition VARCHAR(100)
);

CREATE TABLE phenotype_data (phenotype_id INT PRIMARY KEY,
                         phenotype_name VARCHAR(255) NOT NULL,
                         phenotype_description TEXT);
