tables.table_schema:
CREATE TABLE patients (
    patient_id INT PRIMARY KEY,
    name VARCHAR,
    age INT,
    gender VARCHAR
);

CREATE TABLE genomic_data (
    genomics_id INT PRIMARY KEY,
    patient_id INT,
    dna_sequence TEXT,
    mutation_status VARCHAR
);

CREATE TABLE clinical_data (
    clinical_id INT PRIMARY KEY,
    patient_id INT,
    diagnosis VARCHAR,
    treatment_plan TEXT
);
