tables.table_schema:
CREATE TABLE BiodiversityData (
  id INT PRIMARY KEY,
  species VARCHAR(255),
  location VARCHAR(255),
  date_observed DATE
);

CREATE TABLE Species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(255),
  species_type VARCHAR(100),
  endangerment_level VARCHAR(50)
);

CREATE TABLE ConservationAreas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(255),
  location VARCHAR(100),
  protection_level VARCHAR(50)
);
