tables.table_schema:
CREATE TABLE microbiome_samples (
    sample_id INT PRIMARY KEY,
    species_name VARCHAR(100),
    abundance DECIMAL,
    collection_date DATE
);

CREATE TABLE research_subjects (
    subject_id INT PRIMARY KEY,
    age INT,
    gender CHAR(1),
    health_condition VARCHAR(100)
);

CREATE TABLE bacteria (
  bacteria_id INT PRIMARY KEY,
  bacteria_name STRING,
  bacteria_species STRING,
  bacteria_genus STRING
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  patient_name STRING,
  age INT,
  gender STRING
);

CREATE TABLE researchers (
  researcher_id INT PRIMARY KEY,
  researcher_name STRING,
  institution STRING
);
