tables.table_schema:
CREATE TABLE location_data (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE infection_data (
    infection_id INT PRIMARY KEY,
    patient_id INT REFERENCES patient_data(patient_id),
    infection_type VARCHAR(50),
    detection_date DATE
);

CREATE TABLE spread_analysis (
    analysis_id INT PRIMARY KEY,
    infection_id INT REFERENCES infection_data(infection_id),
    analysis_date DATE,
    most_affected_location VARCHAR(255)
);

CREATE TABLE Locations (
    location_id INT PRIMARY KEY,
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE Diseases (
    disease_id INT PRIMARY KEY,
    name VARCHAR(50),
    description TEXT
);
