tables.table_schema:
CREATE TABLE dna_sequences (
    id INT PRIMARY KEY,
    sequence VARCHAR(255),
    mutation_type VARCHAR(50),
    mutation_location INT
);

CREATE TABLE mutation_analysis (
    sample_id INT PRIMARY KEY,
    mutation_id INT,
    mutation_description VARCHAR(255),
    mutation_result BOOLEAN
);

CREATE TABLE Sequences (
  id INT PRIMARY KEY,
  dna_sequence TEXT
);

CREATE TABLE Mutations (
  id INT PRIMARY KEY,
  sequence_id INT,
  mutation_type VARCHAR(50),
  FOREIGN KEY (sequence_id) REFERENCES Sequences(id)
);

CREATE TABLE MutatedGenes (
  id INT PRIMARY KEY,
  gene_name VARCHAR(50),
  mutation_id INT,
  FOREIGN KEY (mutation_id) REFERENCES Mutations(id)
);

CREATE TABLE dna_sequences (
    id INT PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE results (
    id INT PRIMARY KEY,
    mutation_id INT,
    result_text TEXT,
    FOREIGN KEY (mutation_id) REFERENCES mutations(id)
);
