tables.table_schema:
CREATE TABLE FossilRecords (
  fossil_id INT PRIMARY KEY,
  fossil_name VARCHAR(255),
  fossil_location VARCHAR(255),
  fossil_age INT
);

CREATE TABLE fossil_records (
    fossil_id INT PRIMARY KEY,
    fossil_name STRING,
    fossil_type STRING,
    fossil_age INT
);

CREATE TABLE research_records (
    research_id INT PRIMARY KEY,
    researcher_name STRING,
    research_date DATE,
    research_topic STRING
);
