tables.table_schema:
CREATE TABLE population_dynamics (
  year INT,
  population_size INT,
  genetic_diversity FLOAT
);

CREATE TABLE genetic_changes (
  year INT,
  mutation_rate FLOAT,
  genetic_variation VARCHAR(255)
);

CREATE TABLE Population (
    population_id UUID PRIMARY KEY,
    location VARCHAR(255),
    population_size INT,
    year INT
);
