tables.table_schema:
CREATE TABLE species_dna_sequences (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(100),
    dna_sequence TEXT
);

CREATE TABLE Species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL
);

CREATE TABLE DNASequence (
  sequence_id INT PRIMARY KEY,
  sequence_data TEXT NOT NULL,
  species_id INT NOT NULL,
  FOREIGN KEY (species_id) REFERENCES Species(species_id)
);

CREATE TABLE EvolutionAnalysis (
  analysis_id INT PRIMARY KEY,
  analysis_name VARCHAR(50) NOT NULL
);

CREATE TABLE EvolutionaryTree (
  tree_id INT PRIMARY KEY,
  tree_structure JSON NOT NULL,
  analysis_id INT NOT NULL,
  FOREIGN KEY (analysis_id) REFERENCES EvolutionAnalysis(analysis_id)
);
