tables.table_schema:
CREATE TABLE Species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50) NOT NULL,
    habitat VARCHAR(50),
    diet VARCHAR(50)
);

CREATE TABLE ecological_interactions ( 
  id INT PRIMARY KEY, 
  species_1 VARCHAR(255), 
  species_2 VARCHAR(255), 
  interaction_type VARCHAR(50) 
);

CREATE TABLE species_adaptations ( 
  id INT PRIMARY KEY, 
  species_name VARCHAR(255), 
  adaptation_type VARCHAR(50), 
  adaptation_description TEXT 
);

CREATE TABLE habitat_information ( 
  id INT PRIMARY KEY, 
  habitat_name VARCHAR(255), 
  habitat_type VARCHAR(50), 
 habitat_description TEXT 
);

CREATE TABLE SpeciesAdaptations (
   adaptation_id UUID PRIMARY KEY,
   species_id UUID,
   adaptation_type VARCHAR(50),
   adaptation_description TEXT
);

CREATE TABLE Species (
   species_id UUID PRIMARY KEY,
   species_name VARCHAR(50),
   species_category VARCHAR(50),
   species_description TEXT
);

CREATE TABLE interactions (
    interaction_id SERIAL PRIMARY KEY,
    species1_name VARCHAR(255) NOT NULL,
    species2_name VARCHAR(255) NOT NULL,
    interaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE species (
    species_id SERIAL PRIMARY KEY,
    species_name VARCHAR(255) NOT NULL,
    habitat_type VARCHAR(50) NOT NULL,
    population_size INTEGER
);

CREATE TABLE habitats (
    habitat_id SERIAL PRIMARY KEY,
    habitat_type VARCHAR(50) NOT NULL,
    location VARCHAR(255) NOT NULL,
    temperature_celsius DECIMAL
);

CREATE TABLE adaptations (
    adaptation_id SERIAL PRIMARY KEY,
    species_id INTEGER REFERENCES species(species_id),
    adaptation_type VARCHAR(50) NOT NULL,
    trait_description TEXT
);
