tables.table_schema:
CREATE TABLE allele_frequencies (
  generation INT,
  alleleA_frequency FLOAT,
  alleleB_frequency FLOAT
);

CREATE TABLE Generation (
  generation_id INT PRIMARY KEY,
  population_size INT,
  avg_allele_frequency FLOAT
);

CREATE TABLE allele_frequencies (
    allele_id UUID PRIMARY KEY,
    generation_number INT,
    allele_name VARCHAR,
    frequency FLOAT
);

CREATE TABLE population (
    population_id UUID PRIMARY KEY,
    population_name VARCHAR,
    creation_date DATE,
    total_alleles INT
);

CREATE TABLE generation_changes (
    change_id UUID PRIMARY KEY,
    generation_number INT,
    change_description TEXT,
    affected_alleles INT
);

CREATE TABLE Generation (
    generation_id SERIAL PRIMARY KEY,
    population_id INT NOT NULL,
    allele_frequency FLOAT NOT NULL
);

CREATE TABLE Population (
    population_id SERIAL PRIMARY KEY,
    population_name VARCHAR(50) NOT NULL,
    region VARCHAR(50) NOT NULL
);

CREATE TABLE Allele (
    allele_id SERIAL PRIMARY KEY,
    allele_name VARCHAR(50) NOT NULL
);

CREATE TABLE AlleleFrequency (
    freq_id SERIAL PRIMARY KEY,
    allele_id INT NOT NULL,
    generation_id INT NOT NULL,
    frequency FLOAT NOT NULL
);

CREATE TABLE ChangeLog (
    log_id SERIAL PRIMARY KEY,
    mutation_id INT NOT NULL,
    generation_id INT NOT NULL,
    description TEXT NOT NULL
);
