tables.table_schema:
CREATE TABLE Population (
	population_id UUID PRIMARY KEY,
	population_name VARCHAR(255),
	average_height FLOAT,
	population_size INT
);

CREATE TABLE statistical_analysis (
  analysis_id INT PRIMARY KEY,
  population_id INT,
  attribute VARCHAR(50),
  result FLOAT
);

CREATE TABLE selection_patterns (
  pattern_id INT PRIMARY KEY,
  population_id INT,
  pattern_description TEXT
);

CREATE TABLE population (
  individual_id UUID PRIMARY KEY,
  species VARCHAR(50),
  age INT,
  genetic_traits JSONB
);

CREATE TABLE natural_selection_events (
  event_id UUID PRIMARY KEY,
  population_id UUID,
  selection_pressure NUMERIC,
  event_date TIMESTAMP
);

CREATE TABLE statistical_analysis (
  analysis_id UUID PRIMARY KEY,
  event_id UUID,
  sample_size INT,
  analysis_results JSONB
);

CREATE TABLE population_statistics (
  statistics_id UUID PRIMARY KEY,
  population_id UUID,
  mean_age NUMERIC,
  variance_age NUMERIC
);
