CREATE TABLE species (
	species_id INT PRIMARY KEY,
	species_name VARCHAR(100),
	environment VARCHAR(100),
	population_size INT
);

CREATE TABLE genetic_data (
	genetic_id INT PRIMARY KEY,
	species_id INT REFERENCES species(species_id),
	population INT,
	allele_frequency FLOAT
);

CREATE TABLE Species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR(50) NOT NULL,
  environment VARCHAR(50) NOT NULL
);

CREATE TABLE GeneticDrift (
  drift_id INT PRIMARY KEY,
  num_generations INT NOT NULL,
  rate_of_change FLOAT NOT NULL
);

CREATE TABLE Diversity (
  diversity_id INT PRIMARY KEY,
  species_id INT REFERENCES Species(species_id),
  drift_id INT REFERENCES GeneticDrift(drift_id),
  diversity_index FLOAT NOT NULL
);

CREATE TABLE species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(255),
    environment VARCHAR(255)
);

CREATE TABLE populations (
    population_id INT PRIMARY KEY,
    environment VARCHAR(255),
    population_size INT
);

CREATE TABLE Species (
  species_id INT PRIMARY KEY,
  species_name VARCHAR,
  environment VARCHAR
);

CREATE TABLE GeneticData (
  genetic_id INT PRIMARY KEY,
  species_id INT,
  drift_rate FLOAT,
  FOREIGN KEY (species_id) REFERENCES Species(species_id)
);

CREATE TABLE Environment (
  environment_id INT PRIMARY KEY,
  environment_name VARCHAR
);

CREATE TABLE Population (
  population_id INT PRIMARY KEY,
  species_id INT,
  population_size INT,
  FOREIGN KEY (species_id) REFERENCES Species(species_id)
);
