tables.table_schema:
CREATE TABLE Demographic (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    gender VARCHAR(10),
    date_of_birth DATE
);

CREATE TABLE MedicalHistory (
    patient_id INT,
    medical_condition VARCHAR(100),
    diagnosis_date DATE,
    PRIMARY KEY (patient_id, medical_condition)
);

CREATE TABLE Treatment (
    treatment_id INT PRIMARY KEY,
    patient_id INT,
    treatment_description TEXT,
    treatment_date DATE
);

CREATE TABLE ResearchData (
    research_id SERIAL PRIMARY KEY,
    patient_id INT,
    research_notes TEXT
);

CREATE TABLE ClinicalData (
    clinical_id SERIAL PRIMARY KEY,
    patient_id INT,
    clinical_notes TEXT,
    timestamp TIMESTAMP
);
