tables.table_schema:
CREATE TABLE genome_sequences (
  id INT PRIMARY KEY,
  sequence VARCHAR(255) NOT NULL,
  patient_id INT,
  date_created TIMESTAMP
);

CREATE TABLE GenomeSequences (
    sequence_id INT PRIMARY KEY,
    genetic_code TEXT,
    sequence_length INT
);

CREATE TABLE GeneticVariations (
    variation_id INT PRIMARY KEY,
    gene_name TEXT,
    variation_type TEXT,
    variation_description TEXT
);

CREATE TABLE Diseases (
    disease_id INT PRIMARY KEY,
    disease_name TEXT,
    disease_description TEXT
);
