tables.table_schema:
CREATE TABLE patient_data ( 
  patient_id INT PRIMARY KEY, 
  lab_test_results JSONB, 
  imaging_studies JSONB, 
  patient_reports JSONB 
);

CREATE TABLE lab_tests (
    test_id INT PRIMARY KEY,
    test_name VARCHAR(50),
    test_result VARCHAR(100),
    patient_id INT
);

CREATE TABLE imaging_studies (
    study_id INT PRIMARY KEY,
    study_type VARCHAR(50),
    result_path VARCHAR(200),
    patient_id INT
);

CREATE TABLE lab_tests (
    test_id INT PRIMARY KEY,
    test_name VARCHAR(255),
    patient_id INT,
    test_date DATE
);

CREATE TABLE patient_reports (
    report_id INT PRIMARY KEY,
    patient_id INT,
    report_date DATE,
    report_text TEXT
);

CREATE TABLE patient_reports (
  report_id INT PRIMARY KEY,
  patient_id INT,
  report_text TEXT,
  report_date DATE
);
