tables.table_schema:
CREATE TABLE genetic_sequences (
  id SERIAL PRIMARY KEY,
  sequence TEXT NOT NULL
);

CREATE TABLE sequence_metadata (
  id SERIAL PRIMARY KEY,
  sequence_id INT REFERENCES genetic_sequences(id),
  name VARCHAR(255) NOT NULL,
  description TEXT
);

CREATE TABLE sequence_analysis (
  id SERIAL PRIMARY KEY,
  sequence_id INT REFERENCES genetic_sequences(id),
  analysis_result JSONB
);

CREATE TABLE genes (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    sequence TEXT
);

CREATE TABLE proteins (
    protein_id INT PRIMARY KEY,
    protein_name VARCHAR(255) NOT NULL,
    molecular_weight FLOAT
);

CREATE TABLE sequences (
    sequence_id INT PRIMARY KEY,
    sequence_name VARCHAR(255) NOT NULL,
    gene_id INT,
    sequence_data TEXT
);

CREATE TABLE alignments (
    alignment_id INT PRIMARY KEY,
    alignment_name VARCHAR(255) NOT NULL,
    sequence_id INT,
    alignment_data TEXT
);
