tables.table_schema:
CREATE TABLE disease_information(
    disease_id SERIAL PRIMARY KEY,
    disease_name VARCHAR(100) NOT NULL,
    mutation_id INT NOT NULL
);

CREATE TABLE mutation_data(
    mutation_id SERIAL PRIMARY KEY,
    mutation_name VARCHAR(150) NOT NULL,
    gene_name VARCHAR(100) NOT NULL,
    effect_on_gene VARCHAR(100),
    chromosome_number INT
);

CREATE TABLE mutations (
    id INT PRIMARY KEY,
    gene VARCHAR(50),
    mutation_type VARCHAR(50),
    disease VARCHAR(50)
);

CREATE TABLE disease (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(255)
);

CREATE TABLE mutation (
    mutation_id INT PRIMARY KEY,
    mutation_description TEXT,
    gene_name VARCHAR(100),
    mutation_type VARCHAR(50)
);

CREATE TABLE patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255),
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE patient_mutation (
    patient_id INT,
    mutation_id INT,
    PRIMARY KEY (patient_id, mutation_id),
    FOREIGN KEY (patient_id) REFERENCES patient(patient_id),
    FOREIGN KEY (mutation_id) REFERENCES mutation(mutation_id)
);

CREATE TABLE Disease (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(255) NOT NULL
);

CREATE TABLE Mutation (
    mutation_id INT PRIMARY KEY,
    mutation_name VARCHAR(255) NOT NULL,
    mutation_type VARCHAR(50) NOT NULL
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255) NOT NULL,
    disease_id INT,
    FOREIGN KEY (disease_id) REFERENCES Disease(disease_id)
);

CREATE TABLE Mutation_Association (
    association_id INT PRIMARY KEY,
    mutation_id INT,
    disease_id INT,
    FOREIGN KEY (mutation_id) REFERENCES Mutation(mutation_id),
    FOREIGN KEY (disease_id) REFERENCES Disease(disease_id)
);

CREATE TABLE Clinical_Trial (
    trial_id INT PRIMARY KEY,
    trial_name VARCHAR(255) NOT NULL,
    disease_id INT,
    FOREIGN KEY (disease_id) REFERENCES Disease(disease_id)
);
