tables.table_schema:
CREATE TABLE GeneExpressions (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    expression_level FLOAT NOT NULL
);

CREATE TABLE GeneRegulationPatterns (
    pattern_id INT PRIMARY KEY,
    gene_id INT NOT NULL,
    pattern_description TEXT,
    regulatory_factor VARCHAR(255) NOT NULL
);

CREATE TABLE genes (
    gene_id UUID PRIMARY KEY,
    gene_name VARCHAR(255),
    expression_level FLOAT,
    regulation_type VARCHAR(50)
);

CREATE TABLE experiments (
    experiment_id UUID PRIMARY KEY,
    experiment_date DATE,
    tissue_type VARCHAR(100),
    technique VARCHAR(100)
);

CREATE TABLE gene_data (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(255),
  expression_level FLOAT
);

CREATE TABLE sample_data (
  sample_id INT PRIMARY KEY,
  sample_name VARCHAR(255),
  tissue_type VARCHAR(255)
);

CREATE TABLE regulation_data (
  regulation_id INT PRIMARY KEY,
  gene_id INT,
  regulation_type VARCHAR(50),
  regulator_gene_id INT
);

CREATE TABLE experiment_data (
  experiment_id INT PRIMARY KEY,
  sample_id INT,
  regulation_id INT,
  experiment_date DATE
);

CREATE TABLE GeneList (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    gene_type VARCHAR(50) NOT NULL
);

CREATE TABLE ExpressionLevels (
    sample_id INT PRIMARY KEY,
    gene_id INT REFERENCES GeneList(gene_id),
    expression_level FLOAT NOT NULL
);

CREATE TABLE SampleAnnotations (
    sample_id INT PRIMARY KEY,
    sample_name VARCHAR(255) NOT NULL,
    tissue_type VARCHAR(50) NOT NULL
);

CREATE TABLE GeneRegulationPatterns (
    pattern_id INT PRIMARY KEY,
    gene_id INT REFERENCES GeneList(gene_id),
    regulation_type VARCHAR(50) NOT NULL
);

CREATE TABLE RegulatoryNetwork (
    regulator_id INT REFERENCES GeneList(gene_id),
    target_id INT REFERENCES GeneList(gene_id),
    regulation_strength FLOAT NOT NULL
);
