tables.table_schema:
CREATE TABLE GenomicSequences (
  id INT PRIMARY KEY,
  sequence TEXT,
  species_name VARCHAR(255),
  sequence_source VARCHAR(255)
);

CREATE TABLE Sequence (
    sequence_id SERIAL PRIMARY KEY,
    sequence_name VARCHAR(100) NOT NULL,
    sequence_data TEXT NOT NULL
);

CREATE TABLE Relationship (
    relationship_id SERIAL PRIMARY KEY,
    sequence1_id INT NOT NULL,
    sequence2_id INT NOT NULL,
    relationship_type VARCHAR(50) NOT NULL,
    FOREIGN KEY (sequence1_id) REFERENCES Sequence(sequence_id),
    FOREIGN KEY (sequence2_id) REFERENCES Sequence(sequence_id)
);

CREATE TABLE Sequence (id INT PRIMARY KEY,
sequence_name TEXT,
sequence_data TEXT);

CREATE TABLE ComparisonResults (comparison_id INT PRIMARY KEY,
relationship_id INT,
similarity_score FLOAT);

CREATE TABLE gene_sequences (
    sequence_id INT PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE gene_alignments (
    alignment_id INT PRIMARY KEY,
    sequence_id INT,
    alignment_data TEXT,
    FOREIGN KEY (sequence_id) REFERENCES gene_sequences(sequence_id)
);

CREATE TABLE evolutionary_relationships (
    relationship_id INT PRIMARY KEY,
    alignment_id INT,
    evolution_distance FLOAT,
    FOREIGN KEY (alignment_id) REFERENCES gene_alignments(alignment_id)
);

CREATE TABLE species (
    species_id INT PRIMARY KEY,
    species_name TEXT,
    average_evolution_distance FLOAT
);

CREATE TABLE Genomes (
    genome_id INT PRIMARY KEY,
    species_name VARCHAR(100),
    sequence TEXT
);

CREATE TABLE Mutations (
    mutation_id INT PRIMARY KEY,
    genome_id INT,
    mutation_type VARCHAR(50),
    mutation_position INT,
    CONSTRAINT fk_genome_id FOREIGN KEY (genome_id) REFERENCES Genomes(genome_id)
);

CREATE TABLE EvolutionaryRelationships (
    relationship_id INT PRIMARY KEY,
    genome1_id INT,
    genome2_id INT,
    similarity_score FLOAT,
    CONSTRAINT fk_genome1_id FOREIGN KEY (genome1_id) REFERENCES Genomes(genome_id),
    CONSTRAINT fk_genome2_id FOREIGN KEY (genome2_id) REFERENCES Genomes(genome_id)
);

CREATE TABLE PhylogeneticTrees (
    tree_id INT PRIMARY KEY,
    tree_name VARCHAR(100),
    tree_structure JSON
);

CREATE TABLE SequenceAlignments (
    alignment_id INT PRIMARY KEY,
    genome_id INT,
    alignment_text TEXT,
    CONSTRAINT fk_genome_id FOREIGN KEY (genome_id) REFERENCES Genomes(genome_id)
);
