tables.table_schema:
CREATE TABLE SampleData (
	sample_id INT PRIMARY KEY,
	sample_name TEXT NOT NULL,
	sequencing_data JSONB
);

CREATE TABLE Experiment (
	experiment_id INT PRIMARY KEY,
	sample_id INT REFERENCES SampleData(sample_id),
	experiment_name TEXT NOT NULL,
	experiment_data JSONB
);

CREATE TABLE AnalysisResults (
	result_id INT PRIMARY KEY,
	experiment_id INT REFERENCES Experiment(experiment_id),
	result_data JSONB
);

CREATE TABLE GenomeSamples (
    sample_id UUID PRIMARY KEY,
    experiment_id UUID,
    sequenced_at TIMESTAMP,
    file_path TEXT
);

CREATE TABLE GeneAnnotations (
    annotation_id UUID PRIMARY KEY,
    gene_name TEXT,
    function TEXT,
    pathway TEXT,
    sequence TEXT
);
