tables.table_schema:
CREATE TABLE drug_targets (
    drug_id INT PRIMARY KEY,
    target_gene_id INT,
    target_gene_name VARCHAR(255),
    expression_profile TEXT
);

CREATE TABLE gene_expression_profiles (
    profile_id INT PRIMARY KEY,
    gene_id INT,
    gene_name VARCHAR(255),
    expression_values TEXT
);

CREATE TABLE gene_expression_profiles (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    expression_level FLOAT
);

CREATE TABLE drug_targets (
    target_id INT PRIMARY KEY,
    target_name VARCHAR(255),
    target_function TEXT
);

CREATE TABLE Patients (
  patient_id UUID PRIMARY KEY,
  age INT,
  gender VARCHAR,
  blood_type VARCHAR
);

CREATE TABLE Genes (
  gene_id UUID PRIMARY KEY,
  gene_name VARCHAR,
  chromosome INT,
  chromosomal_location VARCHAR
);

CREATE TABLE GeneExpression ( 
  gene_id INT PRIMARY KEY, 
  gene_name STRING, 
  expression_level FLOAT 
);

CREATE TABLE DrugTargets ( 
  target_id INT PRIMARY KEY, 
  target_name STRING, 
  gene_id INT REFERENCES GeneExpression(gene_id) 
);

CREATE TABLE DrugMolecules ( 
  molecule_id INT PRIMARY KEY, 
  molecule_name STRING, 
  target_id INT REFERENCES DrugTargets(target_id) 
);
