tables.table_schema:
CREATE TABLE regulatory_elements (
  element_id INT PRIMARY KEY,
  element_name VARCHAR(255),
  element_type VARCHAR(100),
  start_position INT,
  end_position INT
);

CREATE TABLE functional_significance (
  significance_id INT PRIMARY KEY,
  element_id INT,
  significance_description TEXT,
  FOREIGN KEY (element_id) REFERENCES regulatory_elements(element_id)
);

CREATE TABLE RegulatoryElements (
  id INT PRIMARY KEY,
  sequence VARCHAR(255),
  element_type VARCHAR(50),
  significance TEXT
);

CREATE TABLE RegulatoryFactors (
  id INT PRIMARY KEY,
  factor_name VARCHAR(100),
  factor_type VARCHAR(50),
  factor_activity VARCHAR(50)
);

CREATE TABLE regulatory_elements (
  element_id SERIAL PRIMARY KEY,
  element_name VARCHAR(100) NOT NULL,
  element_type VARCHAR(50) NOT NULL,
  sequence TEXT NOT NULL
);

CREATE TABLE regulatory_functions (
  function_id SERIAL PRIMARY KEY,
  function_name VARCHAR(100) NOT NULL,
  function_description TEXT
);

CREATE TABLE regulatory_element_function_mapping (
  mapping_id SERIAL PRIMARY KEY,
  element_id INTEGER REFERENCES regulatory_elements(element_id),
  function_id INTEGER REFERENCES regulatory_functions(function_id)
);

CREATE TABLE gene_information (
  gene_id SERIAL PRIMARY KEY,
  gene_name VARCHAR(100) NOT NULL,
  chromosome VARCHAR(10) NOT NULL
);

CREATE TABLE regulatory_elements (
  id SERIAL PRIMARY KEY,
  element_name VARCHAR(100) NOT NULL,
  location VARCHAR(100) NOT NULL,
  sequence TEXT NOT NULL
);

CREATE TABLE functional_significance (
  id SERIAL PRIMARY KEY,
  regulatory_element_id INT REFERENCES regulatory_elements(id),
  significance VARCHAR(100) NOT NULL,
  strength FLOAT
);

CREATE TABLE query_history (
  id SERIAL PRIMARY KEY,
  query TEXT NOT NULL,
  timestamp TIMESTAMP
);

CREATE TABLE user_profiles (
  id SERIAL PRIMARY KEY,
  username VARCHAR(100) UNIQUE NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  full_name VARCHAR(200) NOT NULL
);

CREATE TABLE regulatory_annotations (
  id SERIAL PRIMARY KEY,
  regulatory_element_id INT REFERENCES regulatory_elements(id),
  function VARCHAR(200) NOT NULL,
  annotation TEXT
);
