CREATE TABLE gene_expression_data (
  experiment_id INT PRIMARY KEY,
  gene_id INT,
  expression_level FLOAT
);

CREATE TABLE experimental_metadata (
  experiment_id INT PRIMARY KEY,
  experiment_date DATE,
  researcher_name VARCHAR(50),
  lab_location VARCHAR(50)
);

CREATE TABLE gene_expression_data ( 
    id SERIAL PRIMARY KEY, 
    gene_name VARCHAR(255), 
    expression_level FLOAT 
);

CREATE TABLE experimental_conditions ( 
    id SERIAL PRIMARY KEY, 
    condition_name VARCHAR(255), 
    temperature FLOAT, 
    humidity FLOAT 
);

CREATE TABLE experiment (
  experiment_id INT PRIMARY KEY,
  experiment_name VARCHAR(50) NOT NULL,
  experiment_date DATE,
  researcher_id INT
);

CREATE TABLE researcher (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50) NOT NULL,
  researcher_email VARCHAR(50)
);

CREATE TABLE sample (
  sample_id INT PRIMARY KEY,
  sample_name VARCHAR(50) NOT NULL,
  sample_type VARCHAR(50),
  experiment_id INT,
  FOREIGN KEY (experiment_id) REFERENCES experiment(experiment_id)
);

CREATE TABLE gene (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(50) NOT NULL,
  gene_symbol VARCHAR(10),
  gene_description TEXT
);

CREATE TABLE expression_data (
  data_id INT PRIMARY KEY,
  sample_id INT,
  gene_id INT,
  expression_level FLOAT,
  FOREIGN KEY (sample_id) REFERENCES sample(sample_id),
  FOREIGN KEY (gene_id) REFERENCES gene(gene_id)
);
