    interaction_type STRING
);

CREATE TABLE Pathways (
  pathway_id UUID PRIMARY KEY,
  pathway_name STRING
);

CREATE TABLE Genes (
  gene_id UUID PRIMARY KEY,
  gene_name STRING,
  pathway_id UUID,
  FOREIGN KEY (pathway_id) REFERENCES Pathways(pathway_id)
);

CREATE TABLE Proteins (
  protein_id UUID PRIMARY KEY,
  protein_name STRING,
  gene_id UUID,
  FOREIGN KEY (gene_id) REFERENCES Genes(gene_id)
);

CREATE TABLE pathway (pathway_id INT PRIMARY KEY, pathway_name VARCHAR(255) NOT NULL); 
CREATE TABLE gene (gene_id INT PRIMARY KEY, gene_name VARCHAR(255) NOT NULL, pathway_id INT REFERENCES pathway(pathway_id)); 
CREATE TABLE protein (protein_id INT PRIMARY KEY, protein_name VARCHAR(255) NOT NULL, gene_id INT REFERENCES gene(gene_id)); 
CREATE TABLE interaction (interaction_id INT PRIMARY KEY, interaction_desc VARCHAR(255) NOT NULL, protein1_id INT REFERENCES protein(protein_id), protein2_id INT REFERENCES protein(protein_id)); 
CREATE TABLE pathway_gene_mapping (id INT PRIMARY KEY, pathway_id INT REFERENCES pathway(pathway_id), gene_id INT REFERENCES gene(gene_id));
