tables.table_schema:
CREATE TABLE GenomeData (
 id INT PRIMARY KEY,
 sequence TEXT
);

CREATE TABLE AnnotationData (
 id INT PRIMARY KEY,
 gene_name TEXT,
 start_position INT,
 end_position INT
);

CREATE TABLE genomic_sequences (
    id INT PRIMARY KEY,
    sequence TEXT
);

CREATE TABLE annotations (
    id INT PRIMARY KEY,
    sequence_id INT,
    annotation_text TEXT,
    FOREIGN KEY (sequence_id) REFERENCES genomic_sequences (id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT
);

CREATE TABLE access_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    access_time TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users (user_id)
);

CREATE TABLE sequences (id SERIAL PRIMARY KEY,
sequence TEXT NOT NULL,
length INT NOT NULL);

CREATE TABLE users (id SERIAL PRIMARY KEY,
username TEXT UNIQUE NOT NULL,
password TEXT NOT NULL);

CREATE TABLE permissions (id SERIAL PRIMARY KEY,
user_id INT REFERENCES users(id),
level INT NOT NULL);

CREATE TABLE metadata (id SERIAL PRIMARY KEY,
sequence_id INT REFERENCES sequences(id),
created_at TIMESTAMP NOT NULL);
