tables.table_schema:
CREATE TABLE omics_data_sets (
  data_set_id SERIAL PRIMARY KEY,
  data_set_name VARCHAR(255) NOT NULL,
  data_set_description TEXT,
  data_set_owner VARCHAR(100) NOT NULL
);

CREATE TABLE omics_datasets (
  id SERIAL PRIMARY KEY,
  dataset_name VARCHAR(255) NOT NULL,
  description TEXT,
  created_at TIMESTAMP NOT NULL
);

CREATE TABLE metadata_categories (
  id SERIAL PRIMARY KEY,
  category_name VARCHAR(100) NOT NULL,
  description TEXT
);

CREATE TABLE data_sources ( 
    id INT PRIMARY KEY, 
    source_name VARCHAR(255), 
    source_type VARCHAR(50), 
    source_description TEXT 
);

CREATE TABLE data_relationships ( 
    id INT PRIMARY KEY, 
    data_set_id INT, 
    source_id INT, 
    relationship_type VARCHAR(50) 
);

CREATE TABLE omics_metadata (
    study_id INT PRIMARY KEY,
    study_name VARCHAR(255),
    author VARCHAR(255),
    publication_date DATE
);
