tables.table_schema:
CREATE TABLE sample_data (
    sample_id INT PRIMARY KEY,
    population_id INT,
    variant_id INT,
    genotype VARCHAR
);

CREATE TABLE population (
    population_id INT PRIMARY KEY,
    population_name VARCHAR
);

CREATE TABLE variant (
    variant_id INT PRIMARY KEY,
    variant_type VARCHAR,
    chromosome VARCHAR,
    position INT
);

CREATE TABLE population_studies (
  study_id INT PRIMARY KEY,
  study_name VARCHAR(255) NOT NULL,
  population_name VARCHAR(255) NOT NULL
);

CREATE TABLE samples (
  sample_id INT PRIMARY KEY,
  sample_name VARCHAR(255) NOT NULL,
  study_id INT,
  FOREIGN KEY (study_id) REFERENCES population_studies(study_id)
);

CREATE TABLE Researcher (
  id SERIAL PRIMARY KEY,
  researcher_name VARCHAR(100),
  email VARCHAR(100),
  institution VARCHAR(100)
);
