tables.table_schema:
CREATE TABLE Drug (
    drug_id INT PRIMARY KEY,
    drug_name TEXT,
    target_id INT,
    FOREIGN KEY (target_id) REFERENCES Target(target_id)
);

CREATE TABLE Target (
    target_id INT PRIMARY KEY,
    target_name TEXT,
    target_type TEXT
);

CREATE TABLE drug_interaction (
 drug_id INT,
 target_id INT,
 interaction_type VARCHAR(50)
);

CREATE TABLE drug (
 drug_id INT,
