tables.table_schema:
CREATE TABLE HighThroughputScreeningResults (
    screening_id INT PRIMARY KEY,
    compound_id INT,
    assay_name VARCHAR(255),
    assay_result FLOAT
);

CREATE TABLE Screening_Results (
	id SERIAL PRIMARY KEY,
	screening_id INTEGER REFERENCES High_Throughput_Screening(id),
	date_conducted DATE NOT NULL,
	result_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE Compound_Information (
	id SERIAL PRIMARY KEY,
	compound_name VARCHAR(255) NOT NULL,
	molecular_weight DECIMAL(10,2) NOT NULL,
	smiles_structure TEXT NOT NULL
);

CREATE TABLE Protein_Information (
	id SERIAL PRIMARY KEY,
	protein_name VARCHAR(100) NOT NULL,
	protein_function TEXT,
	gene_name VARCHAR(50) NOT NULL
);

CREATE TABLE screening_results (
    screening_id INT PRIMARY KEY,
    compound_name VARCHAR(255),
    assay_type VARCHAR(255),
    result DECIMAL(10, 2)
);

CREATE TABLE compounds (
    compound_id SERIAL PRIMARY KEY,
    compound_name VARCHAR(255)
);

CREATE TABLE assays (
    assay_id SERIAL PRIMARY KEY,
    assay_name VARCHAR(255)
);

CREATE TABLE screening_assays (
    screening_id INT,
    assay_id INT,
    FOREIGN KEY (screening_id) REFERENCES screening_results(screening_id),
    FOREIGN KEY (assay_id) REFERENCES assays(assay_id)
);

CREATE TABLE compound_screening (
    compound_id INT,
    screening_id INT,
    FOREIGN KEY (compound_id) REFERENCES compounds(compound_id),
    FOREIGN KEY (screening_id) REFERENCES screening_results(screening_id)
);
