tables.table_schema:
CREATE TABLE MetabolomicsData (
  SampleID INT,
  MetaboliteName VARCHAR(255),
  MetaboliteConcentration FLOAT,
  PRIMARY KEY (SampleID)
);

CREATE TABLE MetabolicPathways (
  PathwayID INT,
  PathwayName VARCHAR(255),
  PathwayDescription TEXT,
  PRIMARY KEY (PathwayID)
);

CREATE TABLE metabolomics_samples (
	sample_id INT PRIMARY KEY,
	sample_name VARCHAR(255) NOT NULL,
	metabolite_name VARCHAR(255) NOT NULL,
	concentration FLOAT NOT NULL
);

CREATE TABLE metabolic_pathways (
	pathway_id INT PRIMARY KEY,
	pathway_name VARCHAR(255) NOT NULL,
	related_metabolites TEXT
);

CREATE TABLE disease_mechanisms (
	disease_id INT PRIMARY KEY,
	disease_name VARCHAR(255) NOT NULL,
	disease_description TEXT
);

CREATE TABLE Metabolites (
  metabolite_id SERIAL PRIMARY KEY,
  metabolite_name VARCHAR(255),
  molecular_weight DECIMAL,
  chemical_formula VARCHAR(50)
);

CREATE TABLE Pathways (
  pathway_id SERIAL PRIMARY KEY,
  pathway_name VARCHAR(255),
  description TEXT
);

CREATE TABLE Samples (
  sample_id SERIAL PRIMARY KEY,
  sample_name VARCHAR(255),
  collection_date DATE,
  collection_location VARCHAR(100)
);
