tables.table_schema:
CREATE TABLE GeneAnnotations(
    gene_id SERIAL PRIMARY KEY,
    gene_name VARCHAR(255) NOT NULL,
    go_term VARCHAR(10) NOT NULL
);

CREATE TABLE GeneClassification(
    classification_id SERIAL PRIMARY KEY,
    gene_id INT REFERENCES GeneAnnotations(gene_id),
    functional_category VARCHAR(50) NOT NULL
);

CREATE TABLE gene_annotations (
    gene_id INT,
    gene_symbol VARCHAR(50),
    go_term_id VARCHAR(20),
    go_term_name VARCHAR(100)
);

CREATE TABLE gene_classification (
    gene_id INT,
    functional_category VARCHAR(50),
    classification_type VARCHAR(20)
);

CREATE TABLE gene_information (
    gene_id INT,
    gene_name VARCHAR(100),
    chromosome_id VARCHAR(10),
    gene_length INT
);

CREATE TABLE gene_annotations (
    gene_id INT PRIMARY KEY,
    annotation TEXT
);

CREATE TABLE gene_classification (
    gene_id INT PRIMARY KEY,
    classification TEXT
);

CREATE TABLE go_terms (
    term_id INT PRIMARY KEY,
    term_name TEXT,
    description TEXT
);

CREATE TABLE gene_go_mapping (
    gene_id INT,
    term_id INT,
    FOREIGN KEY (gene_id) REFERENCES gene_annotations(gene_id),
    FOREIGN KEY (term_id) REFERENCES go_terms(term_id)
);

CREATE TABLE GeneAnnotations (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    go_term_id VARCHAR(20),
    go_term_name VARCHAR(255)
);

CREATE TABLE FunctionalClassification (
    classification_id INT PRIMARY KEY,
    classification_name VARCHAR(255)
);

CREATE TABLE GeneInformation (
    gene_id INT PRIMARY KEY,
    gene_symbol VARCHAR(20),
    gene_description TEXT
);

CREATE TABLE GoTermInformation (
    go_term_id VARCHAR(20) PRIMARY KEY,
    go_term_name VARCHAR(255),
    go_term_description TEXT
);
