tables.table_schema:
CREATE TABLE machine_learning_algorithm (
  algorithm_id SERIAL PRIMARY KEY,
  algorithm_name VARCHAR(50) NOT NULL,
  training_data TEXT,
  model_filename VARCHAR(100)
);

CREATE TABLE clinical_trials (
	trial_id INT PRIMARY KEY,
	patient_id INT,
	drug_name VARCHAR(50),
	efficacy_score FLOAT
);

CREATE TABLE patient_data (
	patient_id INT PRIMARY KEY,
	age INT,
	gender VARCHAR(10),
	weight FLOAT
);

CREATE TABLE drug_metadata (
	drug_name VARCHAR(50) PRIMARY KEY,
	drug_type VARCHAR(50),
	drug_company VARCHAR(50)
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  age INT NOT NULL,
  gender VARCHAR(10),
  medical_history TEXT
);

CREATE TABLE drugs (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255) NOT NULL,
  manufacturer VARCHAR(100),
  expiration_date DATE
);
