tables.table_schema:
CREATE TABLE cell_images (
    id INT PRIMARY KEY,
    image_path VARCHAR(255),
    cell_type VARCHAR(50),
    image_data BYTEA
);

CREATE TABLE cell_types (
    cell_type VARCHAR(50) PRIMARY KEY,
    description TEXT
);

CREATE TABLE CellImages (
    image_id INT PRIMARY KEY,
    image_data BYTEA NOT NULL,
    cell_type VARCHAR(50) NOT NULL
);

CREATE TABLE CellLabels (
    label_id INT PRIMARY KEY,
    image_id INT REFERENCES CellImages(image_id),
    label TEXT NOT NULL
);

CREATE TABLE TrainingData (
    training_id INT PRIMARY KEY,
    image_id INT REFERENCES CellImages(image_id),
    label_id INT REFERENCES CellLabels(label_id)
);

CREATE TABLE CellImages (
 image_id INT PRIMARY KEY,
 image_path VARCHAR(255),
 cell_type VARCHAR(50)
);

CREATE TABLE CellFeatures (
 feature_id INT PRIMARY KEY,
 image_id INT,
 feature_value FLOAT,
 FOREIGN KEY (image_id) REFERENCES CellImages(image_id)
);

CREATE TABLE MLModel (
 model_id INT PRIMARY KEY,
 model_name VARCHAR(50),
 model_accuracy FLOAT
);

CREATE TABLE CellImages (
    image_id INT PRIMARY KEY,
    image_data BYTEA,
    cell_type VARCHAR(50)
);

CREATE TABLE CellTypes (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50) UNIQUE
);

CREATE TABLE MachineLearningModels (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(50) UNIQUE,
    model_path VARCHAR(255)
);

CREATE TABLE ImageLabels (
    image_id INT REFERENCES CellImages(image_id),
    type_id INT REFERENCES CellTypes(type_id),
    PRIMARY KEY (image_id, type_id)
);

CREATE TABLE ModelTrainingData (
    model_id INT REFERENCES MachineLearningModels(model_id),
    image_id INT REFERENCES CellImages(image_id),
    type_id INT REFERENCES CellTypes(type_id),
    PRIMARY KEY (model_id, image_id)
);
