tables.table_schema:
CREATE TABLE proteins (
    protein_id INT PRIMARY KEY,
    protein_name VARCHAR(255),
    protein_sequence TEXT
);

CREATE TABLE ligands (
    ligand_id INT PRIMARY KEY,
    ligand_name VARCHAR(255),
    ligand_structure TEXT,
    ligand_interaction_details TEXT
);

CREATE TABLE protein_structures (
    protein_id INT PRIMARY KEY,
    protein_name VARCHAR(255),
    structure_file BLOB
);

CREATE TABLE ligands (
    ligand_id INT PRIMARY KEY,
    ligand_name VARCHAR(255),
    ligand_structure BLOB
);

CREATE TABLE Protein_Structures (
    structure_id INT PRIMARY KEY,
    protein_name VARCHAR(255),
    structure_file BLOB,
    resolution FLOAT
);

CREATE TABLE Researcher (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    affiliation VARCHAR(255)
);

CREATE TABLE protein_structures (
    protein_id INT PRIMARY KEY,
    protein_name TEXT,
    structure_data JSONB
);

CREATE TABLE ligand_interactions (
    interaction_id INT PRIMARY KEY,
    ligand_name TEXT,
    interaction_data JSONB
);

CREATE TABLE protein_ligand_binding (
    binding_id INT PRIMARY KEY,
    protein_id INT REFERENCES protein_structures(protein_id),
    ligand_id INT REFERENCES ligand_interactions(interaction_id),
    binding_score FLOAT
);

CREATE TABLE ligand_properties (
    ligand_id INT PRIMARY KEY REFERENCES ligand_interactions(interaction_id),
    molecular_weight FLOAT,
    chemical_formula TEXT,
    solubility TEXT
);

CREATE TABLE structural_biology_research (
    research_id INT PRIMARY KEY,
    researcher_name TEXT,
    research_topic TEXT
);
