tables.table_schema:
CREATE TABLE protein_structures (
  id INT PRIMARY KEY,
  pdb_id VARCHAR(10),
  structure TEXT,
  resolution FLOAT
);

CREATE TABLE crystallographic_data (
    protein_id UUID PRIMARY KEY,
    structure TEXT,
    resolution FLOAT,
    publication_date DATE
);

CREATE TABLE researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR(50),
    institution VARCHAR(100),
    email VARCHAR(50)
);

CREATE TABLE researcher (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    organization VARCHAR(255),
    email VARCHAR(255)
);
