tables.table_schema:
CREATE TABLE protein_information (
    protein_id INT PRIMARY KEY,
    protein_name TEXT,
    sequence TEXT,
    secondary_structure TEXT
);

CREATE TABLE researcher_information (
    researcher_id INT PRIMARY KEY,
    researcher_name TEXT,
    institution TEXT,
    email TEXT
);

CREATE TABLE proteins (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    sequence TEXT
);

CREATE TABLE researchers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE Protein_Secondary_Structure (
    protein_id INT,
    protein_name VARCHAR(255),
    secondary_structure VARCHAR(255)
);

CREATE TABLE Researcher (
    researcher_id INT,
    researcher_name VARCHAR(255),
    affiliation VARCHAR(255)
);

CREATE TABLE Structures (
    structure_id INT,
    protein_id INT,
    structure_type VARCHAR(255),
    resolution DECIMAL(5, 2)
);
