tables.table_schema:
CREATE TABLE experimental_validation_data (
    validation_id INT PRIMARY KEY,
    protein_id INT,
    experimental_method TEXT,
    validation_result BOOLEAN
);

CREATE TABLE protein_structure_predictions (
    prediction_id INT PRIMARY KEY,
    protein_id INT,
    prediction_method TEXT,
    prediction_score FLOAT
);

CREATE TABLE protein_structure_predictions (
    protein_id SERIAL PRIMARY KEY,
    prediction_method VARCHAR(50) NOT NULL,
    prediction_date DATE,
    confidence_score FLOAT
);

CREATE TABLE experimental_techniques (
    technique_id SERIAL PRIMARY KEY,
    technique_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE Protein (
    protein_id INT PRIMARY KEY,
    protein_name VARCHAR(255),
    experimental_method VARCHAR(255),
    validation_score FLOAT
);

CREATE TABLE ExperimentalData (
    data_id INT PRIMARY KEY,
    protein_id INT,
    experimental_value FLOAT,
    experimental_date DATE
);

CREATE TABLE PredictionResults (
    result_id INT PRIMARY KEY,
    protein_id INT,
    prediction_method VARCHAR(255),
    predicted_score FLOAT
);

CREATE TABLE ValidationParameters (
    parameter_id INT PRIMARY KEY,
    protein_id INT,
    parameter_name VARCHAR(255),
    parameter_value FLOAT
);

CREATE TABLE protein_structure_predictions (
    protein_id INT PRIMARY KEY,
    prediction_method VARCHAR(255),
    prediction_score FLOAT,
    prediction_date DATE
);

CREATE TABLE predicted_structure_quality (
    id INT PRIMARY KEY,
    protein_id INT,
    quality_score FLOAT,
    model_type VARCHAR(255)
);

CREATE TABLE experimental_methods (
    id INT PRIMARY KEY,
    method_name VARCHAR(255),
    description TEXT
);

CREATE TABLE validation_methods (
    id INT PRIMARY KEY,
    method_name VARCHAR(255),
    description TEXT,
    related_protocols TEXT
);
