tables.table_schema:
CREATE TABLE ProteinLigandInteractions (
    protein_id UUID PRIMARY KEY,
    ligand_id UUID,
    interaction_type VARCHAR,
    affinity_score FLOAT
);

CREATE TABLE Researchers (
    researcher_id UUID PRIMARY KEY,
    researcher_name VARCHAR,
    affiliation VARCHAR,
    research_interests VARCHAR
);

CREATE TABLE Proteins (
    protein_id INT PRIMARY KEY,
    protein_name VARCHAR(100) NOT NULL,
    gene_name VARCHAR(100),
    organism VARCHAR(100)
);

CREATE TABLE Ligands (
    ligand_id INT PRIMARY KEY,
    ligand_name VARCHAR(100) NOT NULL,
    chemical_formula VARCHAR(50),
    molecular_weight FLOAT
);
