tables.table_schema:
CREATE TABLE homology_modeling_results (
id INT PRIMARY KEY,
protein_id INT,
model_sequence TEXT,
confidence_score FLOAT
);

CREATE TABLE protein_information (
protein_id INT PRIMARY KEY,
protein_name TEXT,
sequence TEXT
);

CREATE TABLE prediction_parameters (
prediction_id INT PRIMARY KEY,
modeling_tool TEXT,
alignment_tool TEXT
);

CREATE TABLE ProteinSequences (
    id UUID PRIMARY KEY,
    protein_id UUID,
    sequence_text TEXT
);

CREATE TABLE ExperimentalData (
    id UUID PRIMARY KEY,
    protein_id UUID,
    experimental_method VARCHAR(100),
    experimental_results JSONB
);

CREATE TABLE StructuralAlignments (
    id UUID PRIMARY KEY,
    protein_id UUID,
    alignment_results JSONB
);

CREATE TABLE ProteinSequences (
    protein_id SERIAL PRIMARY KEY,
    sequence TEXT NOT NULL
);

CREATE TABLE Models (
    model_id SERIAL PRIMARY KEY,
    protein_id INT REFERENCES ProteinSequences(protein_id),
    model_name VARCHAR(255) NOT NULL,
    model_score FLOAT NOT NULL
);

CREATE TABLE Alignments (
    alignment_id SERIAL PRIMARY KEY,
    model_id INT REFERENCES Models(model_id),
    alignment_result TEXT NOT NULL
);

CREATE TABLE Templates (
    template_id SERIAL PRIMARY KEY,
    template_name VARCHAR(255) NOT NULL,
    template_sequence TEXT NOT NULL
);

CREATE TABLE Evaluation (
    evaluation_id SERIAL PRIMARY KEY,
    model_id INT REFERENCES Models(model_id),
    evaluation_score FLOAT NOT NULL
);
