tables.table_schema:
CREATE TABLE trading_activities (
    trade_id SERIAL PRIMARY KEY,
    trader_name VARCHAR(50) NOT NULL,
    trade_date DATE,
    trade_amount DECIMAL(10, 2) DEFAULT 0.00
);

CREATE TABLE compliance_checks (
    check_id SERIAL PRIMARY KEY,
    trade_id INTEGER REFERENCES trading_activities(trade_id),
    is_compliant BOOLEAN
);

CREATE TABLE traders (
    trader_id INT PRIMARY KEY,
    trader_name VARCHAR(100),
    trader_city VARCHAR(50),
    trader_country VARCHAR(50)
);

CREATE TABLE compliance_checks (
    check_id INT PRIMARY KEY,
    trade_id INT,
    check_description VARCHAR(200),
    check_status VARCHAR(50)
);

CREATE TABLE regulatory_checks (
    check_id INT PRIMARY KEY,
    check_name VARCHAR(255) NOT NULL,
    check_description TEXT
);

CREATE TABLE trade_activities (
    trade_id BIGINT PRIMARY KEY,
    trade_type VARCHAR(50) NOT NULL,
    trade_amount DECIMAL(10,2) NOT NULL,
    trade_date DATE
);

CREATE TABLE regulatory_check_logs (
    log_id INT PRIMARY KEY,
    check_id INT NOT NULL,
    executed_by VARCHAR(100) NOT NULL,
    execution_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE regulatory_approval (
    id SERIAL PRIMARY KEY,
    approval_status VARCHAR(50) NOT NULL,
    approval_date DATE
);

CREATE TABLE trade_activity (
    activity_id SERIAL PRIMARY KEY,
    trade_id INT REFERENCES trade(trade_id),
    activity_type VARCHAR(50) NOT NULL,
    activity_date DATE
);

CREATE TABLE compliance_check (
    check_id SERIAL PRIMARY KEY,
    trade_id INT REFERENCES trade(trade_id),
    check_result VARCHAR(50) NOT NULL,
    check_date DATE
);

CREATE TABLE compliance_officer (
    officer_id SERIAL PRIMARY KEY,
    officer_name VARCHAR(100) NOT NULL,
    officer_role VARCHAR(50) NOT NULL
);
