tables.table_schema:
CREATE TABLE external_sources (
 source_id INT PRIMARY KEY,
 source_name VARCHAR(50),
 source_type VARCHAR(50)
);

CREATE TABLE trade_orders (
    order_id INT PRIMARY KEY,
    order_date DATE,
    order_amount DECIMAL,
    trader_name VARCHAR(50)
);

CREATE TABLE external_data (
    external_id INT PRIMARY KEY,
    external_date DATE,
    external_amount DECIMAL,
    source_name VARCHAR(50)
);

CREATE TABLE reconciliation_log (
    log_id INT PRIMARY KEY,
    reconciled_datetime DATETIME,
    result_status VARCHAR(20),
    error_message TEXT
);

CREATE TABLE matched_trades (
    match_id INT PRIMARY KEY,
    order_id INT,
    external_id INT,
    match_datetime DATETIME
);

CREATE TABLE unreconciled_trades (
    unreconciled_id INT PRIMARY KEY,
    order_id INT,
    external_id INT,
    unreconciled_reason TEXT
);
