tables.table_schema:
CREATE TABLE TraderPreferences (
    preference_id INT PRIMARY KEY,
    trader_id INT REFERENCES TraderProfiles(trader_id),
    preference_category TEXT,
    preference_value TEXT
);

CREATE TABLE TradingRecommendations (
    recommendation_id INT PRIMARY KEY,
    trader_id INT REFERENCES TraderProfiles(trader_id),
    recommendation TEXT,
    confidence_level FLOAT
);
