tables.table_schema:
CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    transaction_time TIMESTAMP,
    transaction_amount DECIMAL(10, 2),
    buyer_id INT,
    seller_id INT
);

CREATE TABLE regulatory_entities (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(50),
    entity_country VARCHAR(50),
    regulatory_status VARCHAR(20)
);

CREATE TABLE traders(
    id SERIAL PRIMARY KEY,
    name VARCHAR(100),
    country VARCHAR(50),
    registration_date DATE
);

CREATE TABLE regulatory_issues(
    id SERIAL PRIMARY KEY,
    issue_date DATE,
    issue_description TEXT
);

CREATE TABLE market_surveillance_systems (
    transaction_id INT PRIMARY KEY,
    transaction_date TIMESTAMP,
    transaction_type VARCHAR(50),
    transaction_amount DECIMAL
);

CREATE TABLE market_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    regulation_description TEXT
);

CREATE TABLE market_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(100),
    participant_type VARCHAR(50)
);

CREATE TABLE market_transactions (
    transaction_id INT PRIMARY KEY,
    participant_id INT,
    transaction_date TIMESTAMP,
    transaction_amount DECIMAL
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    amount DECIMAL,
    product_id INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(50)
);

CREATE TABLE traders (
    trader_id INT PRIMARY KEY,
    trader_name VARCHAR(255),
    country VARCHAR(50)
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255)
);

CREATE TABLE violations (
    violation_id INT PRIMARY KEY,
    violation_description VARCHAR(255),
    transaction_id INT,
    FOREIGN KEY (transaction_id) REFERENCES transactions(transaction_id)
);
